mapper.insert:
insert into planmembers (PlanId, UserId, JoinTime, 
  Score, Flag, IsCreater
  )
values (#{planid,jdbcType=INTEGER}, #{userid,jdbcType=INTEGER}, #{jointime,jdbcType=TIMESTAMP}, 
  #{score,jdbcType=INTEGER}, #{flag,jdbcType=VARCHAR}, #{iscreater,jdbcType=TINYINT}
  )

mapper.select:
SELECT Id 
FROM planmembers
	WHERE UserId = #{uid} AND PlanId = #{pid}

SELECT COUNT(*)
FROM planmembers
	WHERE PlanId = #{pid}
